import React, { useEffect, useState } from 'react';
import { Typography, Box, CircularProgress, Fade } from '@mui/material';
import JobList from '../components/JobList';
import FeaturedJobCard from '../components/FeaturedJobCard';

function Home() {
  const [featuredJob, setFeaturedJob] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetch('/api/jobs/?is_active=true&ordering=-posted_at')
      .then(res => res.json())
      .then(data => {
        setFeaturedJob(data.length > 0 ? data[0] : null);
        setLoading(false);
      })
      .catch(() => {
        setError('Failed to load featured job.');
        setLoading(false);
      });
  }, []);

  return (
    <Box>
      {loading ? (
        <CircularProgress sx={{ mt: 4 }} />
      ) : error ? (
        <Typography color="error">{error}</Typography>
      ) : featuredJob ? (
        <Fade in timeout={800}>
          <Box>
            <FeaturedJobCard job={featuredJob} />
          </Box>
        </Fade>
      ) : null}
      <Typography variant="h5" color="secondary" fontWeight={600} mb={2} mt={4}>
        Latest Jobs
      </Typography>
      <JobList />
      <Typography variant="body1" color="text.secondary">Browse the latest jobs and apply directly to recruiters.</Typography>
    </Box>
  );
}

export default Home;
