import React, { useEffect, useState } from 'react';
import { Box, Fade, IconButton, Button } from '@mui/material';
import EmailIcon from '@mui/icons-material/Email';
import AddPhotoAlternateIcon from '@mui/icons-material/AddPhotoAlternate';

const imageFiles = [
  'add1.jpg', 'add2.jpg', 'add3.jpg', 'add4.jpg', 'add4.png', 'add5.jpg', 'add6.jpg', 'add7.jpg', 'add8.jpg', 'add9.jpg', 'add10.jpg', 'add11.jpg',
  'Screenshot_20260121_150656.png', 'Screenshot_20260121_150656_CXwV0Sy.png', 'Screenshot_20260121_150656_CXwV0Sy_juBGNIa.png',
  'Screenshot_20260121_151549.png', 'Screenshot_20260121_151549_Np0oL37.png'
];

function AdvertBoard() {
  const [current, setCurrent] = useState(0);
  const [isAdmin, setIsAdmin] = useState(false); // TODO: Replace with real auth check
  // For upload
  const [uploading, setUploading] = useState(false);
  const [uploadError, setUploadError] = useState(null);

  // Remove rotation for static banner
  // useEffect(() => {
  //   const timer = setInterval(() => {
  //     setCurrent(c => (c + 1) % imageFiles.length);
  //   }, 5000);
  //   return () => clearInterval(timer);
  // }, []);

  const handleEmail = () => {
    window.location.href = 'mailto:fankodero@gmail.com?subject=Advertise%20on%20CredyJobs&body=I%20would%20like%20to%20advertise%20on%20your%20platform.';
  };

  const handleUpload = (e) => {
    // Placeholder for admin upload logic
    setUploading(true);
    setTimeout(() => {
      setUploading(false);
      setUploadError('Upload feature coming soon!');
    }, 1200);
  };

  if (!imageFiles.length) return null;
  // Show first image as static banner
  const img = imageFiles[0];

  return (
    <Box sx={{ width: '100%', maxWidth: 1800, mx: 'auto', px: 0, display: 'flex', flexDirection: 'column', alignItems: 'center', mt: 2, mb: 2 }}>
      <Box sx={{
        width: '100%',
        maxWidth: 1400,
        height: { xs: 120, sm: 160, md: 180 },
        borderRadius: 8,
        boxShadow: '0 8px 32px 0 rgba(46, 204, 113, 0.18)',
        overflow: 'hidden',
        position: 'relative',
        background: '#eaffea',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        border: '2px solid #1db954',
        transition: 'box-shadow 0.3s',
        '&:hover': {
          boxShadow: '0 16px 48px 0 rgba(46, 204, 113, 0.25)',
        },
      }}>
        <Box sx={{
          width: { xs: 110, sm: 150, md: 180 },
          height: { xs: 90, sm: 120, md: 150 },
          border: '4px solid #1db954',
          borderRadius: 4,
          overflow: 'hidden',
          boxShadow: '0 2px 8px 0 rgba(0,0,0,0.10)',
          mr: 3,
          bgcolor: '#fff',
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'center',
        }}>
          <img
            src={`/media/job_images/${img}`}
            alt="Advert"
            style={{ width: '100%', height: '100%', objectFit: 'contain', borderRadius: 4 }}
          />
        </Box>
        <Box sx={{
          flex: 1,
          display: 'flex',
          flexDirection: 'column',
          justifyContent: 'center',
          alignItems: 'flex-start',
          pl: 2,
        }}>
          <Box sx={{
            bgcolor: 'rgba(29,185,84,0.85)',
            color: '#111',
            px: { xs: 2, sm: 6 },
            py: 1,
            borderRadius: 2,
            fontSize: { xs: 18, sm: 22 },
            fontWeight: 700,
            letterSpacing: 1,
            mb: 1,
          }}>
            Advertise with CredyJobs
          </Box>
          <Box>
            <IconButton
              onClick={handleEmail}
              sx={{ bgcolor: '#fff', color: '#1db954', ml: 0, boxShadow: 2, '&:hover': { bgcolor: '#eaffea' } }}
              color="primary"
            >
              <EmailIcon />
            </IconButton>
            {isAdmin && (
              <Button
                component="label"
                variant="contained"
                color="secondary"
                size="small"
                startIcon={<AddPhotoAlternateIcon />}
                sx={{ ml: 2 }}
                disabled={uploading}
              >
                Upload Banner
                <input type="file" accept="image/*" hidden onChange={handleUpload} />
              </Button>
            )}
          </Box>
        </Box>
      </Box>
      {uploadError && (
        <Box sx={{ color: 'error.main', mt: 1 }}>{uploadError}</Box>
      )}
    </Box>
  );
}

export default AdvertBoard;
