import React from 'react';
import { Card, CardContent, CardMedia, Typography, Button, Box, Chip, Stack, Fade } from '@mui/material';
import { Link as RouterLink } from 'react-router-dom';
import WorkIcon from '@mui/icons-material/Work';
import CalendarMonthIcon from '@mui/icons-material/CalendarMonth';
import PlaceIcon from '@mui/icons-material/Place';

function JobCard({ job }) {
  return (
    <Fade in timeout={600}>
      <Card sx={{ mb: 3, borderRadius: 4, boxShadow: 4, bgcolor: 'background.paper', color: 'text.primary', display: 'flex', flexDirection: { xs: 'column', sm: 'row' }, overflow: 'hidden', position: 'relative' }}>
        {job.image && (
          <CardMedia
            component="img"
            image={job.image.startsWith('http') ? job.image : `/media/${job.image}`}
            alt={job.company}
            sx={{ width: { xs: '100%', sm: 120 }, height: 120, objectFit: 'contain', bgcolor: '#f5f5f5', p: 2, borderRadius: 0 }}
          />
        )}
        <CardContent sx={{ flex: 1 }}>
          <Stack direction="row" spacing={1} alignItems="center" mb={1}>
            <Chip icon={<WorkIcon />} label={job.employment_type?.replace('_', ' ').toUpperCase()} size="small" color="secondary" />
            {job.category && <Chip label={job.category} size="small" color="primary" />}
            <Chip icon={<CalendarMonthIcon />} label={new Date(job.posted_at).toLocaleDateString()} size="small" />
          </Stack>
          <Typography variant="h6" component={RouterLink} to={`/job/${job.id}`} sx={{ textDecoration: 'none', color: 'primary.main', fontWeight: 700, cursor: 'pointer', mb: 0.5 }}>
            {job.title}
          </Typography>
          <Typography variant="subtitle2" color="text.secondary" sx={{ mb: 1 }}>
            <PlaceIcon fontSize="small" sx={{ verticalAlign: 'middle', mr: 0.5 }} />
            {job.company} â€¢ {job.location}
          </Typography>
          <Typography variant="body2" sx={{ mb: 2, color: 'text.secondary' }}>
            {job.description?.slice(0, 120)}{job.description?.length > 120 ? '...' : ''}
          </Typography>
          <Button variant="contained" color="secondary" href={job.apply_url} target="_blank" rel="noopener noreferrer" size="small">
            Apply
          </Button>
        </CardContent>
      </Card>
    </Fade>
  );
}

export default JobCard;
