import React, { useEffect, useState } from 'react';
import { Box, Fade, IconButton, Button } from '@mui/material';
import EmailIcon from '@mui/icons-material/Email';
import AddPhotoAlternateIcon from '@mui/icons-material/AddPhotoAlternate';

const imageFiles = [
  'add1.jpg',
  'add2.jpg',
  'add3.jpg',
  'add4.jpg',
  'add5.jpg',
  'add6.jpg',
  'add7.jpg',
  'add8.jpg',
  'add9.jpg',
  'add10.jpg',
  'add11.jpg'
];

function AdvertBoard() {
  // Each box will have its own carousel index
  const [indices, setIndices] = useState(Array(imageFiles.length).fill(0));
  const [isAdmin, setIsAdmin] = useState(false); // TODO: Replace with real auth check
  // For upload
  const [uploading, setUploading] = useState(false);
  const [uploadError, setUploadError] = useState(null);

  // Carousel animation for images
  useEffect(() => {
    // Each box rotates its own image every 3.5s, but for now, just cycle through the same set for demo
    const timers = indices.map((_, i) => setInterval(() => {
      setIndices(prev => {
        const next = [...prev];
        next[i] = (next[i] + 1) % imageFiles.length;
        return next;
      });
    }, 3500 + i * 200)); // staggered for effect
    return () => timers.forEach(clearInterval);
  }, [indices.length]);

  const handleEmail = () => {
    window.location.href = 'mailto:fankodero@gmail.com?subject=Advertise%20on%20CredyJobs&body=I%20would%20like%20to%20advertise%20on%20your%20platform.';
  };

  const handleUpload = (e) => {
    // Placeholder for admin upload logic
    setUploading(true);
    setTimeout(() => {
      setUploading(false);
      setUploadError('Upload feature coming soon!');
    }, 1200);
  };

  if (!imageFiles.length) return null;
  // Each box will show its own image, rotating through all images

  return (
    <Box sx={{ width: '100%', maxWidth: 1800, mx: 'auto', px: 0, display: 'flex', flexDirection: 'column', alignItems: 'center', mt: 2, mb: 2 }}>
      {/* Header above carousel */}
      <Box sx={{
        width: '100%',
        maxWidth: 1400,
        mb: 1,
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'space-between',
      }}>
        <Box sx={{
          bgcolor: 'rgba(29,185,84,0.85)',
          color: '#111',
          px: { xs: 2, sm: 6 },
          py: 1,
          borderRadius: 2,
          fontSize: { xs: 20, sm: 26 },
          fontWeight: 800,
          letterSpacing: 1,
          boxShadow: '0 2px 8px 0 rgba(0,0,0,0.08)',
        }}>
          Advertise with CredyJobs
        </Box>
        <Box>
          <IconButton
            onClick={handleEmail}
            sx={{ bgcolor: '#fff', color: '#1db954', ml: 2, boxShadow: 2, '&:hover': { bgcolor: '#eaffea' } }}
            color="primary"
          >
            <EmailIcon />
          </IconButton>
          {isAdmin && (
            <Button
              component="label"
              variant="contained"
              color="secondary"
              size="small"
              startIcon={<AddPhotoAlternateIcon />}
              sx={{ ml: 2 }}
              disabled={uploading}
            >
              Upload Banner
              <input type="file" accept="image/*" hidden onChange={handleUpload} />
            </Button>
          )}
        </Box>
      </Box>
      {/* Multiple animated boxes */}
      <Box sx={{
        width: '100%',
        maxWidth: 1400,
        display: 'flex',
        flexDirection: 'row',
        gap: 2,
        justifyContent: 'center',
        alignItems: 'center',
        overflowX: 'auto',
        py: 1,
      }}>
        {imageFiles.map((_, i) => (
          <Fade in key={indices[i]} timeout={800}>
            <Box sx={{
              width: { xs: 70, sm: 90, md: 110 },
              height: { xs: 60, sm: 80, md: 100 },
              border: '3px solid #1db954',
              borderRadius: 3,
              overflow: 'hidden',
              boxShadow: '0 2px 8px 0 rgba(0,0,0,0.10)',
              bgcolor: '#fff',
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center',
              mx: 'auto',
              transition: 'box-shadow 0.3s',
            }}>
              <img
                src={`/media/job_images/${imageFiles[indices[i]]}`}
                alt={`Advert ${i+1}`}
                style={{ width: '100%', height: '100%', objectFit: 'contain', borderRadius: 3 }}
              />
            </Box>
          </Fade>
        ))}
      </Box>
      {uploadError && (
        <Box sx={{ color: 'error.main', mt: 1 }}>{uploadError}</Box>
      )}
    </Box>
  );
}

export default AdvertBoard;
