import React, { useEffect, useState } from 'react';
import { Typography, Box, CircularProgress, Fade } from '@mui/material';
import JobList from '../components/JobList';
import AdvertBoard from '../components/AdvertBoard';
import FeaturedJobCard from '../components/FeaturedJobCard';

function Home() {
  const [featuredJobs, setFeaturedJobs] = useState([]);
  const [fetchError, setFetchError] = useState(null);
  useEffect(() => {
    fetch('/api/jobs/?is_active=true&ordering=-posted_at')
      .then(async res => {
        try {
          if (!res.ok) {
            const text = await res.text();
            throw new Error(`HTTP ${res.status}: ${text}`);
          }
          return await res.json();
        } catch (err) {
          setFetchError('Could not load featured jobs.');
          console.error('Error parsing jobs JSON:', err);
          // Fallback jobs if fetch fails
          const fallbackJobs = [
            {
              id: 1,
              title: 'AI Solutions Architect',
              description: 'Lead AI projects and design scalable solutions',
              location: 'Nairobi',
              company: 'AI Innovators',
              apply_url: 'https://company.com/apply/ai-arch',
              employment_type: 'full_time',
              category: 'Featured',
              is_active: true,
              image: 'job_images/add1.jpg',
              posted_at: new Date().toISOString(),
            },
            {
              id: 2,
              title: 'Senior Data Scientist',
              description: 'Analyze data and build predictive models',
              location: 'Mombasa',
              company: 'DataGenius',
              apply_url: 'https://company.com/apply/data-sci',
              employment_type: 'full_time',
              category: 'Featured',
              is_active: true,
              image: 'job_images/add2.jpg',
              posted_at: new Date().toISOString(),
            },
            {
              id: 3,
              title: 'Creative Marketing Lead',
              description: 'Drive creative campaigns and brand growth',
              location: 'Kisumu',
              company: 'BrandMakers',
              apply_url: 'https://company.com/apply/marketing-lead',
              employment_type: 'full_time',
              category: 'Featured',
              is_active: true,
              image: 'job_images/add3.jpg',
              posted_at: new Date().toISOString(),
            },
            {
              id: 4,
              title: 'Remote DevOps Engineer',
              description: 'Maintain cloud infrastructure remotely',
              location: 'Remote',
              company: 'CloudOps',
              apply_url: 'https://company.com/apply/devops',
              employment_type: 'remote',
              category: 'Featured',
              is_active: true,
              image: 'job_images/add4.jpg',
              posted_at: new Date().toISOString(),
            },
            {
              id: 5,
              title: 'Healthcare Product Manager',
              description: 'Oversee product lifecycle in health tech',
              location: 'Nakuru',
              company: 'HealthBridge',
              apply_url: 'https://company.com/apply/health-pm',
              employment_type: 'full_time',
              category: 'Featured',
              is_active: true,
              image: 'job_images/add5.jpg',
              posted_at: new Date().toISOString(),
            },
          ];
          setFeaturedJobs(fallbackJobs);
          return fallbackJobs;
        }
      })
      .then(data => {
        if (Array.isArray(data)) {
          setFeaturedJobs(data.filter(j => j.category && j.category.toLowerCase().includes('featured')).slice(0, 5));
        }
      });
  }, []);

  return (
    <Box sx={{ width: '100%', minHeight: '100vh', px: 0, py: 0, bgcolor: '#fff', color: '#111', display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
      {/* Featured jobs bar (label removed, bar now occupies space directly) */}
      {featuredJobs.length > 0 && (
        <Box
          sx={{
            width: '100%',
            maxWidth: 1800,
            mt: 3,
            mb: 3,
            px: 2,
            py: 3,
            borderRadius: 5,
            display: 'flex',
            gap: 3,
            overflowX: 'auto',
            boxShadow: '0 12px 40px 0 rgba(30,185,84,0.13)',
            position: 'relative',
            background: 'linear-gradient(90deg, #1db954 0%, #1976d2 100%)',
            border: '2.5px solid #fff',
            zIndex: 2,
            minHeight: 340,
            alignItems: 'flex-start',
            '&:before': {
              content: '""',
              position: 'absolute',
              top: -18,
              left: 30,
              right: 30,
              height: 38,
              borderRadius: 8,
              background: 'linear-gradient(90deg, #fffde7 0%, #e3e9f7 100%)',
              opacity: 0.7,
              zIndex: 1,
              filter: 'blur(8px)',
            },
          }}
        >
          <Box sx={{
            position: 'absolute',
            top: -38,
            left: 32,
            bgcolor: '#fff',
            color: '#1db954',
            px: 4,
            py: 1.6,
            borderRadius: 6,
            fontWeight: 900,
            fontSize: 36,
            letterSpacing: 3,
            boxShadow: '0 4px 24px 0 #1db95466, 0 0 16px 2px #1976d2',
            zIndex: 3,
            border: '3px solid #1db954',
            transform: 'rotate(-2deg) skew(-2deg)',
            textShadow: '0 4px 16px #1976d2, 0 0 12px #1db954',
            filter: 'drop-shadow(0 0 16px #1db95488)',
            transition: 'all 0.2s',
          }}>
            <span style={{ fontSize: 44, marginRight: 12, verticalAlign: 'middle', filter: 'drop-shadow(0 0 8px #ffd700)' }}>ðŸŒŸ</span>
            Featured Jobs
          </Box>
          {featuredJobs.map(job => (
            <Box key={job.id} sx={{ minWidth: 340, maxWidth: 370, flex: '0 0 auto', zIndex: 2 }}>
              <FeaturedJobCard job={job} />
            </Box>
          ))}
        </Box>
      )}
      {/* Main jobs area */}
      <Box sx={{
        width: '100%',
        maxWidth: 1800,
        mb: 4,
        bgcolor: '#fff',
        color: '#111',
        borderRadius: 4,
        boxShadow: '0 8px 32px 0 rgba(60,72,88,0.12)',
        border: '1px solid #e0e0e0',
        px: { xs: 1, sm: 3, md: 6 },
        py: 4,
        minHeight: '80vh',
        display: 'flex',
        flexDirection: 'column',
      }}>
        <Typography variant="h5" color="secondary" fontWeight={600} mb={2} mt={2}>
          Latest Jobs
        </Typography>
        <JobList />
        <Typography variant="body1" color="text.secondary" sx={{ mt: 2 }}>Browse the latest jobs and apply directly to recruiters.</Typography>
      </Box>
      {/* Adverts before footer */}
      <Box sx={{ width: '100%', maxWidth: 1800, mb: 2 }}>
        <AdvertBoard />
      </Box>
    </Box>
  );
}

export default Home;
