import React, { useEffect, useState } from 'react';
import { Box, Fade, IconButton } from '@mui/material';
import EmailIcon from '@mui/icons-material/Email';

const imageFiles = [
  'add1.jpg', 'add2.jpg', 'add3.jpg', 'add4.jpg', 'add5.jpg', 'add6.jpg', 'add7.jpg', 'add8.jpg', 'add9.jpg', 'add10.jpg', 'add11.jpg',
  'Screenshot_20260121_150656.png', 'Screenshot_20260121_150656_CXwV0Sy.png', 'Screenshot_20260121_150656_CXwV0Sy_juBGNIa.png', 'Screenshot_20260121_151549.png', 'Screenshot_20260121_151549_Np0oL37.png', 'add4.png'
];

function AdvertBoard() {
  const [current, setCurrent] = useState(0);

  useEffect(() => {
    const timer = setInterval(() => {
      setCurrent(c => (c + 1) % imageFiles.length);
    }, 4000);
    return () => clearInterval(timer);
  }, []);

  const handleEmail = () => {
    window.location.href = 'mailto:fankodero@gmail.com?subject=Advertise%20on%20CredyJobs&body=I%20would%20like%20to%20advertise%20on%20your%20platform.';
  };

  if (!imageFiles.length) return null;

  const img = imageFiles[current];

  return (
    <Box sx={{ width: '100vw', maxWidth: '100%', px: 0, display: 'flex', justifyContent: 'center', alignItems: 'center', mt: 6, mb: 2 }}>
      <Fade in timeout={800}>
        <Box sx={{
          width: { xs: '100vw', sm: '90vw', md: '80vw', lg: '70vw' },
          height: { xs: 180, sm: 260, md: 320 },
          borderRadius: 6,
          boxShadow: '0 12px 40px 0 rgba(0,114,255,0.18)',
          overflow: 'hidden',
          position: 'relative',
          background: 'linear-gradient(90deg,#e3f2fd 0%,#fff 100%)',
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'center',
          transition: 'box-shadow 0.3s',
          '&:hover': {
            boxShadow: '0 20px 60px 0 rgba(0,114,255,0.25)',
          },
        }}>
          <img
            src={`/media/job_images/${img}`}
            alt="Advert"
            style={{ width: '100%', height: '100%', objectFit: 'cover', borderRadius: 6, transition: 'transform 0.5s cubic-bezier(.4,2,.6,1)', boxShadow: '0 4px 24px 0 rgba(0,0,0,0.10)' }}
          />
          <Box sx={{
            position: 'absolute',
            bottom: 0,
            left: 0,
            width: '100%',
            bgcolor: 'rgba(0,0,0,0.32)',
            color: '#fff',
            px: 3,
            py: 1,
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'space-between',
            borderBottomLeftRadius: 6,
            borderBottomRightRadius: 6,
            backdropFilter: 'blur(2px)',
          }}>
            <span style={{ fontWeight: 600, fontSize: 18 }}>Advertise with CredyJobs</span>
            <IconButton
              onClick={handleEmail}
              sx={{ bgcolor: '#fff', color: '#0072ff', ml: 2, boxShadow: 2, '&:hover': { bgcolor: '#e3f2fd' } }}
              color="primary"
            >
              <EmailIcon />
            </IconButton>
          </Box>
        </Box>
      </Fade>
    </Box>
  );
}

export default AdvertBoard;
