import React, { useEffect, useState } from 'react';
import { Box, Fade, IconButton, Button } from '@mui/material';
import EmailIcon from '@mui/icons-material/Email';
import AddPhotoAlternateIcon from '@mui/icons-material/AddPhotoAlternate';


// Accept images as a prop or from backend in future
function getAdvertImages() {
  // Placeholder: fetch from backend or props in future
  return [];
}


function AdvertBoard({ images }) {
  // Use images from props or backend, fallback to empty array
  const imageFiles = Array.isArray(images) ? images.slice(0, 13) : getAdvertImages();
  const [isAdmin, setIsAdmin] = useState(false); // TODO: Replace with real auth check
  const [uploading, setUploading] = useState(false);
  const [uploadError, setUploadError] = useState(null);

  const handleEmail = () => {
    window.location.href = 'mailto:fankodero@gmail.com?subject=Advertise%20on%20CredyJobs&body=I%20would%20like%20to%20advertise%20on%20your%20platform.';
  };

  const handleUpload = (e) => {
    // Placeholder for admin upload logic
    setUploading(true);
    setTimeout(() => {
      setUploading(false);
      setUploadError('Upload feature coming soon!');
    }, 1200);
  };

  // If no images, just render empty boxes for now (max 15)
  // Animated conveyor belt effect for advert boxes
  const [offset, setOffset] = useState(0);
  useEffect(() => {
    if (!imageFiles.length) return;
    const timer = setInterval(() => {
      setOffset(prev => (prev + 1) % imageFiles.length);
    }, 5000);
    return () => clearInterval(timer);
  }, [imageFiles.length]);

  return (
    <Box sx={{ width: '100%', maxWidth: 1800, mx: 'auto', px: 0, display: 'flex', flexDirection: 'column', alignItems: 'center', mt: 2, mb: 2 }}>
      <Box sx={{
        width: '100%',
        maxWidth: 1400,
        mb: 1,
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'space-between',
      }}>
        <Box sx={{
          bgcolor: 'rgba(29,185,84,0.85)',
          color: '#111',
          px: { xs: 2, sm: 6 },
          py: 1,
          borderRadius: 2,
          fontSize: { xs: 20, sm: 26 },
          fontWeight: 800,
          letterSpacing: 1,
          boxShadow: '0 2px 8px 0 rgba(0,0,0,0.08)',
        }}>
          Advertise with CredyJobs
        </Box>
      </Box>
      <Box sx={{
        width: '100%',
        maxWidth: 1400,
        display: 'flex',
        flexDirection: 'row',
        gap: 2,
        justifyContent: 'center',
        alignItems: 'center',
        overflowX: 'auto',
        py: 1,
      }}>
        {Array.from({ length: Math.min(imageFiles.length, 13) }).map((_, i) => {
          const imgIndex = (offset + i) % imageFiles.length;
          return (
            <Box key={i} sx={{
              width: 170,
              height: 150,
              border: '3px solid #1db954',
              borderRadius: 6,
              bgcolor: 'rgba(36,255,140,0.10)',
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center',
              mx: 'auto',
              opacity: 0.9,
              boxShadow: '0 4px 32px 0 rgba(30,255,180,0.18), 0 0 0 2px #00ffb3 inset',
              backdropFilter: 'blur(2.5px)',
              borderImage: 'linear-gradient(135deg, #1db954 40%, #00ffb3 100%) 1',
              transition: 'box-shadow 0.3s, border-color 0.3s',
              position: 'relative',
            }}>
              {imageFiles[imgIndex] && (
                <img
                  src={`/media/job_images/${imageFiles[imgIndex]}`}
                  alt={`Advert ${imgIndex + 1}`}
                  style={{ width: '100%', height: '100%', objectFit: 'contain', borderRadius: 6 }}
                  onError={e => { e.target.onerror = null; e.target.src = '/media/job_images/add1.jpg'; }}
                />
              )}
            </Box>
          );
        })}
      </Box>
    </Box>
  );

  return (
    <Box sx={{ width: '100%', maxWidth: 1800, mx: 'auto', px: 0, display: 'flex', flexDirection: 'column', alignItems: 'center', mt: 2, mb: 2 }}>
      {/* Header above carousel */}
      <Box sx={{
        width: '100%',
        maxWidth: 1400,
        mb: 1,
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'space-between',
      }}>
        <Box sx={{
          bgcolor: 'rgba(29,185,84,0.85)',
          color: '#111',
          px: { xs: 2, sm: 6 },
          py: 1,
          borderRadius: 2,
          fontSize: { xs: 20, sm: 26 },
          fontWeight: 800,
          letterSpacing: 1,
          boxShadow: '0 2px 8px 0 rgba(0,0,0,0.08)',
        }}>
          Advertise with CredyJobs
        </Box>
        <Box>
          <IconButton
            onClick={handleEmail}
            sx={{ bgcolor: '#fff', color: '#1db954', ml: 2, boxShadow: 2, '&:hover': { bgcolor: '#eaffea' } }}
            color="primary"
          >
            <EmailIcon />
          </IconButton>
          {isAdmin && (
            <Button
              component="label"
              variant="contained"
              color="secondary"
              size="small"
              startIcon={<AddPhotoAlternateIcon />}
              sx={{ ml: 2 }}
              disabled={uploading}
            >
              Upload Banner
              <input type="file" accept="image/*" hidden onChange={handleUpload} />
            </Button>
          )}
        </Box>
      </Box>
      {/* Multiple animated boxes */}
      <Box sx={{
        width: '100%',
        maxWidth: 1400,
        display: 'flex',
        flexDirection: 'row',
        gap: 2,
        justifyContent: 'center',
        alignItems: 'center',
        overflowX: 'auto',
        py: 1,
      }}>
        {imageFiles.map((_, i) => (
          <Fade in key={indices[i]} timeout={800}>
            <Box sx={{
              width: 170,
              height: 150,
              border: '3px solid #1db954',
              borderRadius: 6,
              overflow: 'hidden',
              boxShadow: '0 8px 32px 0 rgba(30,255,180,0.18), 0 0 0 2px #00ffb3 inset',
              bgcolor: 'rgba(36,255,140,0.10)',
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center',
              mx: 'auto',
              transition: 'box-shadow 0.3s, border-color 0.3s',
              borderImage: 'linear-gradient(135deg, #1db954 40%, #00ffb3 100%) 1',
              backdropFilter: 'blur(2.5px)',
              position: 'relative',
              '::after': {
                content: '""',
                position: 'absolute',
                inset: 0,
                borderRadius: 6,
                pointerEvents: 'none',
                boxShadow: '0 0 24px 4px #00ffb3 inset',
                opacity: 0.18,
              },
            }}>
              <img
                src={`/media/job_images/${imageFiles[indices[i]]}`}
                alt={`Advert ${i+1}`}
                style={{ width: '100%', height: '100%', objectFit: 'contain', borderRadius: 6 }}
                onError={e => { e.target.onerror = null; e.target.src = '/media/job_images/add1.jpg'; }}
              />
            </Box>
          </Fade>
        ))}
      </Box>
      {uploadError && (
        <Box sx={{ color: 'error.main', mt: 1 }}>{uploadError}</Box>
      )}
    </Box>
  );
}

export default AdvertBoard;
