import React, { useEffect, useState } from 'react';
import { Box, Fade, IconButton, Button, TextField, InputAdornment } from '@mui/material';
import EmailIcon from '@mui/icons-material/Email';
import AddPhotoAlternateIcon from '@mui/icons-material/AddPhotoAlternate';



function AdvertBoard() {
  const [adverts, setAdverts] = useState([]);
  const [search, setSearch] = useState('');
  const [isAdmin, setIsAdmin] = useState(false); // TODO: Replace with real auth check
  const [uploading, setUploading] = useState(false);
  const [uploadError, setUploadError] = useState(null);
  const [offset, setOffset] = useState(0);

  useEffect(() => {
    fetch('/api/adverts/?is_active=true')
      .then(res => res.json())
      .then(data => setAdverts(data))
      .catch(() => setAdverts([]));
  }, []);

  useEffect(() => {
    if (!adverts.length) return;
    const timer = setInterval(() => {
      setOffset(prev => (prev + 1) % adverts.length);
    }, 5000);
    return () => clearInterval(timer);
  }, [adverts.length]);

  const handleEmail = () => {
    window.location.href = 'mailto:fankodero@gmail.com?subject=Advertise%20on%20CredyJobs&body=I%20would%20like%20to%20advertise%20on%20your%20platform.';
  };

  const handleUpload = (e) => {
    setUploading(true);
    setTimeout(() => {
      setUploading(false);
      setUploadError('Upload feature coming soon!');
    }, 1200);
  };

  // Filter adverts by search
  const filteredAdverts = adverts.filter(a =>
    a.title.toLowerCase().includes(search.toLowerCase()) ||
    (a.description && a.description.toLowerCase().includes(search.toLowerCase()))
  );

  return (
    <Box sx={{ width: '100%', maxWidth: 1800, mx: 'auto', px: 0, display: 'flex', flexDirection: 'column', alignItems: 'center', mt: 2, mb: 2 }}>
      <Box sx={{ width: '100%', maxWidth: 1400, mb: 1, display: 'flex', alignItems: 'center', justifyContent: 'space-between' }}>
        <Box sx={{ bgcolor: 'rgba(29,185,84,0.85)', color: '#111', px: { xs: 2, sm: 6 }, py: 1, borderRadius: 2, fontSize: { xs: 20, sm: 26 }, fontWeight: 800, letterSpacing: 1, boxShadow: '0 2px 8px 0 rgba(0,0,0,0.08)' }}>
          Advertise with CredyJobs
        </Box>
        <Box>
          <IconButton onClick={handleEmail} sx={{ bgcolor: '#fff', color: '#1db954', ml: 2, boxShadow: 2, '&:hover': { bgcolor: '#eaffea' } }} color="primary">
            <EmailIcon />
          </IconButton>
          {isAdmin && (
            <Button component="label" variant="contained" color="secondary" size="small" startIcon={<AddPhotoAlternateIcon />} sx={{ ml: 2 }} disabled={uploading}>
              Upload Banner
              <input type="file" accept="image/*" hidden onChange={handleUpload} />
            </Button>
          )}
        </Box>
      </Box>
      {/* Searchbar */}
      <Box sx={{ width: '100%', maxWidth: 600, mb: 2 }}>
        <TextField
          fullWidth
          variant="outlined"
          placeholder="Search adverts..."
          value={search}
          onChange={e => setSearch(e.target.value)}
          InputProps={{
            startAdornment: <InputAdornment position="start"><EmailIcon sx={{ color: '#1db954' }} /></InputAdornment>,
            style: { background: '#fff', fontWeight: 700, color: '#111' }
          }}
        />
      </Box>
      {/* Animated advert boxes */}
      <Box sx={{ width: '100%', maxWidth: 1400, display: 'flex', flexDirection: 'row', gap: 2, justifyContent: 'center', alignItems: 'center', overflowX: 'auto', py: 1 }}>
        {filteredAdverts.slice(offset, offset + 13).map((advert, i) => (
          <Fade in key={advert.id} timeout={800}>
            <Box sx={{ width: 170, height: 150, border: '3px solid #1db954', borderRadius: 6, bgcolor: 'rgba(36,255,140,0.10)', display: 'flex', alignItems: 'center', justifyContent: 'center', mx: 'auto', opacity: 0.9, boxShadow: '0 4px 32px 0 rgba(30,255,180,0.18), 0 0 0 2px #00ffb3 inset', backdropFilter: 'blur(2.5px)', borderImage: 'linear-gradient(135deg, #1db954 40%, #00ffb3 100%) 1', transition: 'box-shadow 0.3s, border-color 0.3s', position: 'relative' }}>
              {advert.image && (
                <img
                  src={advert.image.startsWith('http') ? advert.image : `/media/${advert.image}`}
                  alt={advert.title}
                  style={{ width: '100%', height: '100%', objectFit: 'contain', borderRadius: 6 }}
                  onError={e => { e.target.onerror = null; e.target.src = '/media/job_images/add1.jpg'; }}
                />
              )}
            </Box>
          </Fade>
        ))}
      </Box>
      {uploadError && (
        <Box sx={{ color: 'error.main', mt: 1 }}>{uploadError}</Box>
      )}
    </Box>
  );
}

export default AdvertBoard;
